import sun.misc.BASE64Decoder;

import javax.swing.*;
import java.awt.*;
import java.io.IOException;

// 注释，解释说明，不是代码 Eclipse  IDEA
// 我们先弄清楚Swing能做啥？----测试！
public class SwingGUI
{
    // 程序入口，程序从这里开始运行！
    public static void main(String[] args) throws IOException
    {
        // 创建一个窗口
        JFrame frame = new JFrame();
        // 设置让窗口关闭同时退出程序
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        // 设置大小
        frame.setSize(600, 800);
        // 设置标题
        frame.setTitle("微信客户端");
        
        String s = "";
        ImageIcon image = new ImageIcon(new BASE64Decoder().decodeBuffer(s));
        
        // 添加一个文本(文本中显示一张图片)
        JLabel label = new JLabel(image);
        //label.setText("这是一个微信客户端（扫码领取福利！）");
        // 设置字体
        label.setFont(new Font("微软雅黑", Font.BOLD, 32));
        // 将文本添加到我们的窗口
        frame.getContentPane().add(label);
        
        // 按钮 JavaFX
//        JButton button = new JButton("点我一下有福利");
//        frame.getContentPane().add(button);
        
        // 让这个窗口显示出来
        frame.setVisible(true);
    }
}
